// Require menu
const axios = require('axios');
const https = require('https');

// Code menu
const getting = (http) => {
  axios
    .get(http)
    .then(res => {
      console.log(`statusCode: ${res.status}`) // Print Bypass information
      console.log(res)
    })
    .catch(error => {
      console.error(error)
    })
 };
 const getport = (page) => {
   const options = {
    hostname: page,
    port: 443,
    path: '/todos',
    method: 'GET'
 }

   const req = https.request(options, res => {
     console.log(`statusCode: ${res.statusCode}`)

      res.on('data', d => {
      process.stdout.write(d)
    })
   })

    req.on('error', error => {
    console.error(error)
  })

  req.end()
 }
// Module exports menu
exports.getting = getting;
exports.getport = getport;
